function wpdm_embed(url){var frameid=wpdm_frame_id("wpdm");document.write("<iframe id='"+frameid+'\' style="width: 100%; border: 0;" src="'+url+"&frameid="+frameid+'"></iframe>')}function wpdm_frame_id(prefix,more_entropy){if(typeof prefix==="undefined"){prefix=""}var retId;var formatSeed=function(seed,reqWidth){seed=parseInt(seed,10).toString(16);if(reqWidth<seed.length){return seed.slice(seed.length-reqWidth)}if(reqWidth>seed.length){return Array(1+(reqWidth-seed.length)).join("0")+seed}return seed};if(!this.php_js){this.php_js={}}if(!this.php_js.uniqidSeed){this.php_js.uniqidSeed=Math.floor(Math.random()*123456789)}this.php_js.uniqidSeed++;retId=prefix;retId+=formatSeed(parseInt((new Date).getTime()/1e3,10),8);retId+=formatSeed(this.php_js.uniqidSeed,5);if(more_entropy){retId+=(Math.random()*10).toFixed(8).toString()}return retId}function wpdm_adjust_frame_height(id,height){document.getElementById(id).style.height=height+"px"}function hideLockFrame(){var element=document.getElementById("wpdm-lock-frame");element.parentNode.removeChild(element)}if(window.addEventListener){window.addEventListener("message",listenMsg,false)}else if(window.attachEvent){window.attachEvent("onmessage",listenMsg,false)}function listenMsg(event){var data=event.data;if(data.task==="showiframe"){window.document.body.innerHTML+=data.iframe}if(data.task==="hideiframe"){hideLockFrame()}}